function.sample:
var list = {};
list = {

        els : [],

        addedItem : JMVC.Event.create(list),

        deletedItem : JMVC.Event.create(list),

        add : function (s) {

            this.els.push(s) &&

            this.addedItem.notify({item : s});

        },

        del : function (i) {

            this.els.splice(i, 1) &&

            this.deletedItem.notify({index : i});

        }

    };

list.addedItem.attach(function () {console.log(arguments)});

list.deletedItem.attach(function () {console.log(arguments)});

list.add('a');

list.add('b');

list.add('c');

list.del(1);

console.log(list.els);

function.code:
var list = {};
list = {
    els : [],
    addedItem : JMVC.Event.create(list),
    deletedItem : JMVC.Event.create(list),
    add : function (s) {
        if(this.els.push(s)) this.addedItem.notify({item : s});
    },
    del : function (i) {
        if (this.els.splice(i, 1)) this.deletedItem.notify({index : i});
    }
};
list.addedItem.attach(function () {console.log(arguments)});
list.deletedItem.attach(function () {console.log(arguments)});
list.add('a');
list.add('b');
list.add('c');
list.del(1);
console.log(list.els);
