function.sample:
(function () {

    var p = JMVC.Promise.create();

    console.log('uan');

    window.setTimeout(function () {p.done(1234); }, 1000);

    return p;

})().then(function (p) {

    console.log('two ', p.result);

    window.setTimeout(function () {p.done(~~p.result * 2); }, 1000);

}).then(function (p) {

    console.log('three ', p.result);

    p.done();

}).then(tryJoin);



function tryJoin() {

    console.debug('--- now a trial for Promise.join ---');

    JMVC.Promise.join(

        function () {

            var p = JMVC.Promise.create();

            window.setTimeout(function () {

                p.done(1);

            }, 1000);

            return p;

        },

        function () {

            var p = JMVC.Promise.create();

            window.setTimeout(function () {

                p.done(2);

            }, 2000);

            return p;

        }

    ).then(function (r) {

        console.debug(r);

    });

}

function.code:
(function () {
    var p = JMVC.Promise.create();
    console.log('uan');
    window.setTimeout(function () {p.done(1234); }, 1000);
    return p;
})().then(function (p) {
    console.log('two ', p.result);
    window.setTimeout(function () {p.done(~~p.result * 2); }, 1000);
}).then(function (p) {
    console.log('three ', p.result);
    p.done();
}).then(tryJoin);

function tryJoin() {
    console.debug('--- now a trial for Promise.join ---');
    JMVC.Promise.join(
        function () {
            var p = JMVC.Promise.create();
            window.setTimeout(function () {
                p.done(1);
            }, 1000);
            return p;
        },
        function () {
            var p = JMVC.Promise.create();
            window.setTimeout(function () {
                p.done(2);
            }, 2000);
            return p;
        }
    ).then(function (r) {
        console.debug(r);
    });
}
