function.sample:
var trg = JMVC.dom.find('.intro')[0];

JMVC.css.hide(trg);

window.setTimeout(function () { // show it back

    JMVC.css.show(trg);         // after 1 s

},1000);

function.code:
var trg = JMVC.dom.find('.intro')[0];
JMVC.css.hide(trg);
window.setTimeout(function () {
    JMVC.css.show(trg);
},1000);
