function.sample:
/**

* inject two modules: app/extensions/def/xxx.js

* and app/extensions/def/yyy.js

*/

var t = JMVC.define(

    'xxx.sss',

    ['def.xxx', 'def.yyy'],

    function (x, y) {

        return {

            one : function () {

                alert(y.getName() + ' extends ' + x.name);

            }

        }

    }

);

JMVC.xxx.sss.one();

console.debug(t);

function.code:
var t = JMVC.define('xxx.sss', ['def.xxx', 'def.yyy'], function (x, y) {
    return {
        one : function () {
            alert(y.getName() + ' extends ' + x.name);
        }
    }
});
JMVC.xxx.sss.one();
console.debug(t);
