function.sample:
JMVC.set('a', 10);

JMVC.debug(JMVC.get('a'));

JMVC.del('a');

JMVC.debug(JMVC.get('a'));



JMVC.set('a', 11, localStorage);

JMVC.debug(JMVC.get('a', localStorage));

JMVC.del('a', localStorage);

JMVC.debug(JMVC.get('a', localStorage));



JMVC.set('a', 12, sessionStorage);

JMVC.debug(JMVC.get('a', sessionStorage));

JMVC.del('a', sessionStorage);

JMVC.debug(JMVC.get('a', sessionStorage));
