function.sample:
// INNER HOOK

JMVC.hook({'onAfterRender': function (){alert('You`ll never see that message booking that function from the console!!!'); }});

// USER DEFINED HOOK

function foo() {

    JMVC.hookCheck('myhook', [1,2,3]);

};

JMVC.hook({'myhook': function () {console.debug(arguments); }}, true);

foo();
