function Complex(re, im) {

    this._re = re;

    this._im = im;

    this._rho = Math.sqrt(re * re + im * im, 2);

    this._theta = Math.atan2(im, re);

    this._mod = null;

    this.toString = function (){

        return this._re +

            (this._im >= 0 ? ' + ' : ' - ') +

            Math.abs(this._im) +'i';

    }

};

JMVC.prototipize(Complex, {

    'mod' : function () {

        this._mod = Math.sqrt(

            this._re * this._re +

            this._im * this._im

        );

        return this._mod;

    },

    'add' : function (cplx) {

        return new Complex(

            this._re + cplx._re,

            this._im + cplx._im

        );

    }

});

var c1 = new Complex(3, 4),

    c2 = new Complex(7, 2);

console.debug(c1.mod());

console.debug(c1.add(c2) + '');

