var list = {

        els : [],

        addedItem : new JMVC.Event(list),

        deletedItem : new JMVC.Event(list),

        add : function (s) {

        this.els.push(s) &&

        this.addedItem.notify({item : s});

    },

    del : function (i) {

    this.els.splice(i, 1) &&

    this.deletedItem.notify({index : i});

    }

};

list.addedItem.attach(function () {console.debug(arguments)});

list.deletedItem.attach(function () {console.debug(arguments)});

list.add('a');

list.add('b');

list.add('c');

list.del(1);

list.els;

