function.sample:
var trg = JMVC.dom.find('.intro')[0];

JMVC.css.style(trg, 'backgroundColor', 'coral');

console.log('absolute: ', JMVC.css.getPosition(trg));

console.log('viewport rel: ', JMVC.css.getPosition(trg, true));

function.code:
var trg = JMVC.dom.find('.intro')[0];
JMVC.css.style(trg, 'backgroundColor', 'coral');
console.log('absolute: ', JMVC.css.getPosition(trg));
console.log('viewport rel: ', JMVC.css.getPosition(trg, true));
